+Klass* JavaThread::security_get_caller_class(int depth) {
+  ResetNoHandleMark rnhm;
+  HandleMark hm(Thread::current());
+
+  vframeStream vfst(this);
+  vfst.security_get_caller_frame(depth);
+  if (!vfst.at_end()) {
+    return vfst.method()->method_holder();
