-    //   take a while to process their first tick).
-    if (PeriodicTask::num_tasks() > 0) {
-      WatcherThread::start();
-    }
-  }
-
-  create_vm_timer.end();
-#ifdef ASSERT
-  _vm_complete = true;
-#endif
-
-  if (DumpSharedSpaces) {
-    MetaspaceShared::preload_and_dump();
-    ShouldNotReachHere();
-  }
-
-  return JNI_OK;
-}
