-        if (Yields > 5) {
-          os::naked_short_sleep(1);
-        } else {
-          os::naked_yield();
-          ++Yields;
-        }
-      } else {
-        SpinPause();
-      }
-    }
-    if (Atomic::cmpxchg(adr, 0, 1) == 0) return;
+    if (Atomic::cmpxchg(adr, 0, 1) == 0) return;
