+static const char* get_java_version_info(InstanceKlass* ik,
+                                         Symbol* field_name) {
+  fieldDescriptor fd;
+  bool found = ik != NULL &&
+               ik->find_local_field(field_name,
+                                    vmSymbols::string_signature(), &fd);
+  if (found) {
+    oop name_oop = ik->java_mirror()->obj_field(fd.offset());
+    if (name_oop == NULL) {
