-
-instruct vasrB(vReg dst, vReg shift) %{
-  predicate(UseSVE > 0);
-  match(Set dst (RShiftVB dst shift));
-  ins_cost(SVE_COST);
-  format %{ "sve_asr $dst, $dst, $shift\t# vector (sve) (B)" %}
-  ins_encode %{
-    __ sve_asr(as_FloatRegister($dst$$reg), __ B,
-         ptrue, as_FloatRegister($shift$$reg));
-  %}
-  ins_pipe(pipe_slow);
-%}
