-    Assembler::SIMD_RegVariant size = __ elemType_to_regVariant(bt);
-    __ sve_cntp(rscratch1, size, ptrue, as_PRegister($pg$$reg));
-    __ sve_whilelo(as_PRegister($dst$$reg), size, zr, rscratch1);
-  %}
