+BINARY_OP_PREDICATE(vaddB, AddVB, sve_add,  B)
+BINARY_OP_PREDICATE(vaddS, AddVS, sve_add,  H)
+BINARY_OP_PREDICATE(vaddI, AddVI, sve_add,  S)
+BINARY_OP_PREDICATE(vaddL, AddVL, sve_add,  D)
