+// vector sub
+BINARY_OP(vsubB, SubVB, subv, sve_sub,  B)
+BINARY_OP(vsubS, SubVS, subv, sve_sub,  H)
+BINARY_OP(vsubI, SubVI, subv, sve_sub,  S)
+BINARY_OP(vsubL, SubVL, subv, sve_sub,  D)
+BINARY_OP(vsubF, SubVF, fsub, sve_fsub, S)
