+dnl
+dnl BITWISE_OP($1,        $2,      $3,        $4      )
+dnl BITWISE_OP(rule_name, op_name, insn_neon, insn_sve)
+define(`BITWISE_OP', `
+instruct $1(vReg dst, vReg src1, vReg src2) %{
+  match(Set dst ($2 src1 src2));
