+  match(Set dst (LShiftVS src (LShiftCntV shift)));
+  match(Set dst (LShiftVI src (LShiftCntV shift)));
+  match(Set dst (LShiftVL src (LShiftCntV shift)));
+  format %{ "vlsl_imm $dst, $src, $shift" %}
