+  predicate(UseSVE > 0 && Matcher::vector_element_basic_type(n) == T_BYTE);
+  effect(TEMP_DEF dst, TEMP tmp1, TEMP tmp2, TEMP tmp3, TEMP tmp4, TEMP ptmp, TEMP pgtmp);
+  match(Set dst (CompressV src pg));
