+  predicate(UseSVE > 0 && Matcher::vector_element_basic_type(n) == T_SHORT);
+  effect(TEMP_DEF dst, TEMP tmp1, TEMP tmp2, TEMP pgtmp);
+  match(Set dst (CompressV src pg));
