+    __ sve_sub($dst$$FloatRegister, size, 1);
+    // dst = 4 0 0 5 6 0 7 8
+    __ sve_tbl($dst$$FloatRegister, size, $src$$FloatRegister, $dst$$FloatRegister);
+  %}
+  ins_pipe(pipe_slow);
+%}

