+  }
+
+  if (!i2->is_con()) {
+    return nullptr;
+  }
+  julong i2_con = i2->get_con();
+  if (i2_con == 1) {
+    return nullptr;
+  }
+  if (phase->type(in(1))->is_long()->is_con()) {
