files.patch:
@@ -149,0 +149,2 @@
+      // qv_ovf || rc < qv is the test for rc < qv with overflow taken into
+      // consideration
@@ -156,2 +158,1 @@
-    T new_rc = rc * 2;
-    if (new_rc < rc || new_rc > d) {
+    if (rc > d - rc) {  // 2 * rc > d
@@ -159,3 +160,3 @@
-      c = c * 2 - 1;
-      rc = new_rc - d;
-    } else {
+      c += c - 1;
+      rc += rc - d;     // rc = 2 * rc - d
+    } else {            // 2 * rc <= d
@@ -163,2 +164,2 @@
-      c = c * 2;
-      rc = new_rc;
+      c += c;
+      rc += rc;         // rc = 2 * rc
@@ -166,3 +167,1 @@
-
-    T new_rv = rv * 2;
-    if (new_rv < rv || new_rv >= v) {
+    if (rv >= v - rv) { // 2 * rv >= v
@@ -170,3 +169,3 @@
-      qv = qv * 2 + 1;
-      rv = new_rv - v;
-    } else {
+      qv += qv + 1;
+      rv += rv - v;     // rv = 2 * rv - v
+    } else {            // 2 * rv < v
@@ -174,2 +173,2 @@
-      qv = qv * 2;
-      rv = new_rv;
+      qv += qv;
+      rv += rv;         // rv = 2 * rv
