-            byte data[] = readBytes(4, istream);
-            int data0 = (int) data[0] & 0xFF;
-            int data1 = (int) data[1] & 0xFF;
-            int data2 = (int) data[2] & 0xFF;
-            int data3 = (int) data[3] & 0xFF;
