@@ -33,0 +33,8 @@
+static unsigned int calculate_hash(address stack[NMT_TrackingStackDepth]) {
+  uintptr_t hash = 0;
+  for (int i = 0; i < NMT_TrackingStackDepth; i++) {
+    hash += (uintptr_t)stack[i];
+  }
+  return hash;
+}
