+#
+# Compares two year* test data files
+# Typical usage:
+#     perl CompareYearData.pl ../../TimeZoneData/year2008 year2008
+#
+
+%oldData = &readData($ARGV[0]);
+%newData = &readData($ARGV[1]);
+
+foreach $key (sort(keys(%oldData))) {
+  if (defined($newData{$key})) {
+    if ($oldData{$key} ne $newData{$key}) {
+      print "Changed:\n";
+      print "$oldData{$key}";
+      print "---\n";
+      print "$newData{$key}";
+    }
+    delete $newData{$key};
+  } else {
+    print "Deleted:\n";
+    print "$oldData{$key}";
+  }
+}
+foreach $key (sort(keys(%newData))) {
+  print "Added:\n";
+  print "$newData{$key}";
+}
+
+sub readData {
+  local($file) = @_;
+
+  open(F, $file) || die "Can't open $file\n";
+  my %data = ();
+  my $line = 0;
+  my $id = "";
+
+  while (<F>) {
+    $line++;
+    s/^\s*\d+ //;
+    if (/tzdata\d+/) {
+      $data{" version"} = $_;
+      next;
+    }
+    if (/(\s*#.*$)/) {
+      $data{" comments"} .= $_;
+      next;
+    }
+    if (/^(Zone|Rule)/) {
+      die "No id at line: $line\n" if ($id eq "");
+      $data{$id} .= $_;
+    } elsif (/^(\S+)\s+\S+/) {
+      $id = $1;
+      $data{$id} = $_;
+      $flag = 1;
+    } else {
+      die "Unknown format at line: $line: $file\n";
+    }
+  }
+  close(F);
+  return %data;
+}

