+sub readIDs {
+    local ($file) = @_;
+    local (@ids, $i);
+
+    open(F, $file) || die "Fatal: can't open $file.\n";
+
+    $i = 0;
+    while (<F>) {
+	chop;
+	if (/^\#/) { # skip comment line
+	    next;
+	}
+
+	# trim any leading and trailing space
+	s/^\s+//;
+	s/\s+$//;
+
+        if (/^\s*$/) { # skip blank line
+	    next;
+	}
+
+	$ids[$i++] = $_;
+    }
+    close(F);
+    return @ids;
+}
