-                while (!isEngineClosed(serverEngine)) {
-                    int len;
-
-                    // Inbound data
-                    log("================");
-
-                    // Read from the Client side.
-                    try {
-                        len = is.read(inbound);
-                        if (len == -1) {
-                            logSocketStatus(clientSocket);
-                            if (clientSocket.isClosed()
-                                    || clientSocket.isOutputShutdown()) {
