+    private Object params[] = null;
+
+    private void invokeRecurse(int depth) throws Exception {
+        if (method == null) {
+            //
+            // Optimization trick: allocate once, use everywhere.
+            //
+            method = Stack018.class.getMethod("recurse");
+            params = new Object[]{};
