+    public static void main(String[] args) {
+        // Create a new CompileFramework instance.
+        CompileFramework comp = new CompileFramework();
+
+        // Generate 10 files.
+        for (int i = 0; i < 10; i++) {
+            comp.addJasmSourceCode("p.xyz.XYZ" + i, generate(i));
