+import java.util.concurrent.CountDownLatch;
+
+/*
+ * @test
+ * @bug 5097243
+ * @summary Tests that FileDialog can be closed by ESC any time
+ * @key headful
+ * @run main DoubleActionESC
+ */
+
+public class DoubleActionESC {
+    private static Frame f;
+    private static Button showBtn;
+    private static FileDialog fd;
+    private static Robot robot;
+    private static volatile Point p;
+    private static volatile Dimension d;
+    private static volatile CountDownLatch latch;
+    private static final int REPEAT_COUNT = 2;
+
+    public static void main(String[] args) throws Exception {
+        latch = new CountDownLatch(1);
+
+        robot = new Robot();
+        robot.setAutoDelay(100);
+        try {
+            EventQueue.invokeAndWait(() -> {
+                createAndShowUI();
+            });
+
+            robot.delay(1000);
+            EventQueue.invokeAndWait(() -> {
+                p = showBtn.getLocationOnScreen();
+                d = showBtn.getSize();
+            });
+
+            for (int i = 0; i < REPEAT_COUNT; ++i) {
+                Thread thread = new Thread(() -> {
+                    robot.mouseMove(p.x + d.width / 2, p.y + d.height / 2);
+                    robot.mousePress(InputEvent.BUTTON1_DOWN_MASK);
+                    robot.mouseRelease(InputEvent.BUTTON1_DOWN_MASK);
+                });
+                thread.start();
+                robot.delay(3000);
+
+                Thread thread1 = new Thread(() -> {
+                    robot.keyPress(KeyEvent.VK_ESCAPE);
+                    robot.keyRelease(KeyEvent.VK_ESCAPE);
+                    robot.waitForIdle();
+                });
+                thread1.start();
+                robot.delay(3000);
+            }
+
+            latch.await();
+            if (fd.isVisible()) {
+                throw new RuntimeException("File Dialog is not closed");
+            }
+        } finally {
+            EventQueue.invokeAndWait(() -> {
+                if (f != null) {
+                    f.dispose();
+                }
+            });
+        }
+    }
+
+    public static void createAndShowUI() {
+        f = new Frame("DoubleActionESC Test");
+        showBtn = new Button("Show File Dialog");
+        fd = new FileDialog(f);
+        showBtn.addActionListener(new ActionListener() {
+            @Override
+            public void actionPerformed(ActionEvent e) {
+                if (e.getSource() == showBtn) {
+                    fd.setSize(200, 200);
+                    fd.setLocation(200, 200);
+                    fd.setVisible(true);
+                    latch.countDown();
+                }
+            }
+        });
+        f.add(showBtn);
+        f.setSize(300, 200);
+        f.setLocationRelativeTo(null);
+        f.setVisible(true);
+    }
+}

