+                robot.mouseRelease(InputEvent.BUTTON3_DOWN_MASK);
+            }
+
+            // To close the popup, otherwise test fails on windows with timeout error
+            robot.mouseMove(panelCenter.x - 5, panelCenter.y - 5);
