+
+    private static TableModel getTableModel2() {
+        String[] columnNames = new String[30];
+        String[][] data = new String[1][30];
+
+        for (int i = 0; i < columnNames.length; i++) {
+            columnNames[i] = "Column " + i;
+            data[0][i] = "Data " + 1;
