+        for (int i = 0; i < vectors.length; ++i) {
+            GlyphVector gv = vectors[i];
+            Rectangle2D bounds = gv.getLogicalBounds();
+            g2d.drawGlyphVector(gv, x, (float)(y - bounds.getY()));
+            y += bounds.getHeight();
+        }
+    }
+  }
