+  constexpr julong bad = 0xBAD;
+  julong x = bad;
+
+  fill_file(test_file, "foo 100");
+  bool is_ok = controller->read_numerical_key_value(base_with_slash, "foo", &x);
+  EXPECT_TRUE(is_ok);
+  EXPECT_EQ((julong)100, x);
+
