+  EXPECT_EQ(bad, foo) << "foo was altered";
+
+  // Some interface files have numbers as well as the string
+  // 'max', which means unlimited.
+  jlong result = -10;
+  fill_file(test_file, "max\n");
+  ok = controller->read_number_handle_max(base_with_slash, &result);
