+    // helper methods because jdk21 functionality is not allowed
+    Name getLastHelper(List<Name> l) {
+        if (l.isEmpty()) {
+            throw new NoSuchElementException();
+        } else {
+            return l.get(l.size() - 1);
+        }
+    }
+
+    Name removeLastHelper(List<Name> l) {
+        if (l.isEmpty()) {
+            throw new NoSuchElementException();
+        } else {
+            return l.remove(l.size() - 1);
+        }
+    }
