+void PlatformSupport::updatePreferences() const {
+    jobject newPreferences = collectPreferences();
+
+    jboolean preferencesChanged =
+        newPreferences != NULL &&
+        !env->CallBooleanMethod(newPreferences, jObjectEquals, preferences);
