+        if (!EXCEPTION_OCCURED(env)) {
+            env->CallVoidMethod(application, jApplicationNotifyPreferencesChanged, unmodifiablePreferences);
+            env->DeleteLocalRef(unmodifiablePreferences);
+        }
+    }
+
+    env->DeleteLocalRef(newPreferences);
+}

