+    // gtk sends both smooth and non-smooth events
+    if (gdk_window_get_events(gdk_window) & GDK_SMOOTH_SCROLL_MASK) {
+        is_smooth = true;
+        if (event->direction == GDK_SCROLL_SMOOTH) {
+            // Note: For gtk negatives values means UP/LEFT, JavaFx is opposite
+            dx = -event->delta_x;
+            dy = -event->delta_y;
@@ -395,0 +396,1 @@
+    }
