-        } else if (event->direction == GDK_SCROLL_DOWN) {
@@ -403,0 +403,2 @@
+        } else if (event->direction == GDK_SCROLL_DOWN) {
+            dy = -1;
@@ -404,2 +406,0 @@
-            dx = -1;
-        } else if (event->direction == GDK_SCROLL_RIGHT) {
@@ -407,0 +407,2 @@
+        } else if (event->direction == GDK_SCROLL_RIGHT) {
+            dx = -1;
@@ -417,4 +419,0 @@
-        // Note: For gtk negatives values means UP/LEFT, JavaFx is opposite
-        dx = (dx != 0) ? dx * -1 : 0;
