+    }
+
+    private void f(double x) {
+        sb.append(numberFormat.format(x));
+    }
+
+    private void dumpBounds(Rectangle2D r) {
+        sb.append("{x=");
+        f(r.getMinX());
+        sb.append(", y=");
+        f(r.getMinY());
+        sb.append(", w=");
+        f(r.getWidth());
+        sb.append(", h=");
+        f(r.getHeight());
+        sb.append("}");
+    }
+
+    private void dumpScreen(Screen screen, boolean primary, int num) {
+        sb.append("  ");
+        if (primary) {
+            sb.append("Primary ");
+        }
+        sb.append("Screen");
+        if (!primary) {
+            sb.append(" #");
+            sb.append(num);
+        }
+        sb.append(":");
+        nl();
+        sb.append("    dpi=");
+        f(screen.getDpi());
+        nl();
+        sb.append("    bounds=");
+        dumpBounds(screen.getBounds());
+        nl();
+        sb.append("    visual bounds=");
+        dumpBounds(screen.getVisualBounds());
+        nl();
+        sb.append("    output.scale.x=");
+        f(screen.getOutputScaleX());
+        nl();
+        sb.append("    output.scale.y=");
+        f(screen.getOutputScaleY());
+        nl();
+    }
+
+    private String collect() {
+        // system properties
+        header("System Properties");
+        {
+            Properties p = System.getProperties();
+            ArrayList<String> keys = new ArrayList<>(p.stringPropertyNames());
+            Collections.sort(keys);
+            for (String k: keys) {
+                String v = System.getProperty(k);
+                sb.append(k);
+                sb.append("=");
+                safe(v);
+                sb.append('\n');
+            }
+        }
+
+        // environment
+        sb.append('\n');
+        header("Environment");
+        {
+            Map<String, String> env = System.getenv();
+            ArrayList<String> keys = new ArrayList<>(env.keySet());
+            Collections.sort(keys);
+            for (String k: keys) {
+                String v = env.get(k);
+                sb.append(k);
+                sb.append("=");
+                safe(v);
+                sb.append('\n');
+            }
+        }
+
+        // screens
+        sb.append('\n');
+        header("Screens");
+        {
+            int num = 1;
+            Screen primary = Screen.getPrimary();
+            dumpScreen(primary, true, num++);
+
+            List<Screen> screens = Screen.getScreens();
+            for (Screen s: screens) {
+                if (!s.equals(primary)) {
+                    dumpScreen(s, false, num++);
+                }
+            }
+        }
+        return sb.toString();
+    }
+}

