+    /**
+     * This is to address https://bugs.openjdk.org/browse/JDK-8303478
+     */
+    private void commitValueOnFocusLost(){
+        try {
+            commitValue();
+        } catch (DateTimeParseException dtpe) {
+            cancelEdit();
+        }
+    }
