files.patch:
@@ -134,1 +134,1 @@
-             * Scroll one cell further in the direction the user has clicked if only one cell is shown.
+             * Scroll the block increment further in the direction the user has clicked if only one cell is shown.
@@ -140,1 +140,0 @@
-                int index = firstVisibleCell.getIndex();
@@ -142,1 +141,1 @@
-                    flow.scrollTo(index - 1);
+                    flow.scrollPixels(-flow.getBlockIncrement());
@@ -144,1 +143,1 @@
-                    flow.scrollTo(index + 1);
+                    flow.scrollPixels(flow.getBlockIncrement());

@@ -1555,10 +1555,8 @@
-            if (targetIndex < 0) {
-                T cell = getCell(targetIndex);
-                setMaxPrefBreadth(Math.max(getMaxPrefBreadth(), getCellBreadth(cell)));
-                if (downOrRight) {
-                    scrollPixels(getCellLength(cell));
-                } else {
-                    // up or left
-                    scrollPixels(-getCellLength(cell));
-                }
-                releaseCell(cell);
+            T cell = getAvailableCell(targetIndex);
+            setCellIndex(cell, targetIndex);
+            resizeCell(cell);
+            setMaxPrefBreadth(Math.max(getMaxPrefBreadth(), getCellBreadth(cell)));
+            cell.setVisible(true);
+            if (downOrRight) {
+                cells.addLast(cell);
+                scrollPixels(getCellLength(cell));
@@ -1566,13 +1564,3 @@
-                T cell = getAvailableCell(targetIndex);
-                setCellIndex(cell, targetIndex);
-                resizeCell(cell);
-                setMaxPrefBreadth(Math.max(getMaxPrefBreadth(), getCellBreadth(cell)));
-                cell.setVisible(true);
-                if (downOrRight) {
-                    cells.addLast(cell);
-                    scrollPixels(getCellLength(cell));
-                } else {
-                    // up or left
-                    cells.addFirst(cell);
-                    scrollPixels(-getCellLength(cell));
-                }
+                // up or left
+                cells.addFirst(cell);
+                scrollPixels(-getCellLength(cell));
@@ -1815,0 +1803,10 @@
+    /**
+     * The amount of pixels by which to adjust the position when the the scroll bar track is clicked.
+     *
+     * @return the value in pixels
+     */
+    public double getBlockIncrement() {
+        // For reasons of backward compatibility, we use the cell length of the empty cell (cell index -1)
+        return getCellLength(-1);
+    }
+
