-
-        if (lastTime < 0) {
-            lastTime = System.nanoTime();
-            elapsedTime = 0;
-            elapsedFrames = 0;
-            totalElapsedTime = 0;
-            totalElapsedFrames = 0;
-            return;
-        }
-
-        long currTime = System.nanoTime();
-        elapsedTime += currTime - lastTime;
-        elapsedFrames += 1;
-        totalElapsedTime += currTime - lastTime;
-        totalElapsedFrames += 1;
-
-        double elapsedSeconds = elapsedTime / 1e9;
