+    private static String fmt(String s) {
+        if (s == null) {
+            return "<null>";
+        } else if (s.length() == 1) {
+            char c = s.charAt(0);
+            if (c > 0x20) {
+                return s;
+            }
+        }
+
+        StringBuilder sb = new StringBuilder();
+        sb.append('<');
+        for (int i = 0; i < s.length(); i++) {
+            char c = s.charAt(i);
+            sb.append("\\u");
+            sb.append(hex(c >> 4));
+            sb.append(hex(c));
+        }
+        sb.append('>');
+        return sb.toString();
+    }
+
+    private static char hex(int nibble) {
+        return "0123456789ABCDEF".charAt(nibble & 0x0f);
+    }

