-            if (advance) {
-                int ix = getActiveCellIndex();
-                ix++;
-                if (ix < cellPanes.size()) {
-                    CellPane next = cellPanes.get(ix);
-                    setActiveCellPane(next);
-                    next.focusLater();
-                }
-            }
