+ */
+public enum TextStyle {
+    TITLE,
+    HEADING,
+    SUBHEADING,
+    BODY,
+    MONOSPACED;
+
+    public String getDisplayName() {
+        switch (this) {
+        case BODY:
+            return "Body";
+        case HEADING:
+            return "Heading";
+        case MONOSPACED:
+            return "Monospaced";
+        case TITLE:
+            return "Title";
+        case SUBHEADING:
+            return "Subheading";
+        default:
+            return "?" + this;
+        }
+    }
+
+    public static StringConverter<TextStyle> converter() {
+        return new StringConverter<TextStyle>() {
+            @Override
+            public String toString(TextStyle t) {
+                return t == null ? null : t.getDisplayName();
+            }
+
+            @Override
+            public TextStyle fromString(String s) {
+                for (TextStyle t : TextStyle.values()) {
+                    if (s.equals(t.getDisplayName())) {
+                        return t;
+                    }
+                }
+                return TextStyle.BODY;
+            }
+        };
+    }
