+    public RichEditorDemoWindow() {
+        pane = new RichEditorDemoPane();
+
+        status = new Label();
+        status.setPadding(new Insets(2, 10, 2, 10));
+
+        BorderPane bp = new BorderPane();
+        bp.setTop(createMenu());
