+
+        setScene(scene);
+        setWidth(1200);
+        setHeight(600);
+
+        pane.control.caretPositionProperty().addListener((x) -> {
+            updateStatus();
+        });
+        pane.actions.modifiedProperty().addListener((x) -> {
+            updateTitle();
+        });
+        pane.actions.fileNameProperty().addListener((x) -> {
+            updateTitle();
+        });
+        updateStatus();
+        updateTitle();
