+            setBackground(Color.LIGHTGREEN).
+            build());
+        m.nl();
+
+        m.addSegment(" Translucent Background Color");
+        m.setParagraphAttributes(StyleAttributeMap.builder().
+            setBackground(FX.alpha(Color.LIGHTGREEN, 0.5)).
+            build());
+        m.nl();
+
