+package com.oracle.demo.richtext.settings;
+
+import java.util.ArrayList;
+
+/**
+ * Represents a string property as a stream of objects.
+ *
+ * @author Andy Goryachev
+ */
+public abstract class SStream {
+
+    public abstract Object[] toArray();
+
+    private SStream() {
+    }
+
+    public static SStream writer() {
+        return new SStream() {
+            private ArrayList<Object> items = new ArrayList<>();
+
+            @Override
+            protected void addValue(Object x) {
+                items.add(x);
+            }
+
+            @Override
+            public Object[] toArray() {
+                return items.toArray();
+            }
+        };
+    }
+
+    public static SStream reader(Object[] items) {
+        return new SStream() {
+            int index;
+
+            @Override
+            protected Object nextValue() {
+                if (index >= items.length) {
+                    return null;
+                }
+                return items[index++];
+            }
+
+            @Override
+            public Object[] toArray() {
+                return items;
+            }
+        };
+    }
+
+    public void add(int x) {
+        addValue(x);
+    }
+
+    public void add(double x) {
+        addValue(x);
+    }
+
+    public void add(String x) {
+        addValue(x);
+    }
+
+    protected void addValue(Object x) {
+        throw new UnsupportedOperationException();
+    }
+
+    protected Object nextValue() {
+        throw new UnsupportedOperationException();
+    }
+
+    public final String nextString(String defaultValue) {
+        Object v = nextValue();
+        if (v instanceof String s) {
+            return s;
+        }
+        return defaultValue;
+    }
+
+    public final double nextDouble(double defaultValue) {
+        Object v = nextValue();
+        if (v instanceof String s) {
+            try {
+                return Double.parseDouble(s);
+            } catch (NumberFormatException e) {
+                // ignore
+            }
+        } else if (v instanceof Double d) {
+            return d;
+        }
+        return defaultValue;
+    }
+
+    public final int nextInt(int defaultValue) {
+        Object v = nextValue();
+        if (v instanceof String s) {
+            try {
+                return Integer.parseInt(s);
+            } catch (NumberFormatException e) {
+                // ignore
+            }
+        } else if (v instanceof Integer d) {
+            return d;
+        }
+        return defaultValue;
+    }
+
+    @Override
+    public String toString() {
+        StringBuilder sb = new StringBuilder(64);
+        sb.append("[");
+        boolean sep = false;
+        for (Object x: toArray()) {
+            if (sep) {
+                sb.append(",");
+            } else {
+                sep = true;
+            }
+            sb.append(x);
+        }
+        sb.append("]");
+        return sb.toString();
+    }
+}

