+    protected TextPos previousWord(RichTextArea control, TextPos pos) {
+        int index = pos.index();
+        int offset = pos.offset();
+        BreakIterator br = null;
+
+        for (;;) {
+            if ((index == 0) && (offset <= 0)) {
+                return TextPos.ZERO;
+            }
+
+            String text = getPlainText(index);
+            if (text.length() == 0) {
+                index--;
+                offset = Integer.MAX_VALUE;
+                continue;
+            }
+
+            if (br == null) {
+                br = BreakIterator.getWordInstance();
+            }
+            br.setText(text);
+
+            int len = text.length();
+            int off = br.preceding(Utils.clamp(0, offset, len));
+
+            while (off != BreakIterator.DONE && !RichUtils.isLetterOrDigit(text, off)) {
+                off = br.preceding(Utils.clamp(0, off, len));
+            }
+
+            if (off < 0) {
+                index--;
+                offset = Integer.MAX_VALUE;
+                continue;
+            }
+            return new TextPos(index, off);
+        }
