+    protected PathElement[] getRangeTop() {
+        double w = getWidth();
+        return new PathElement[] {
+            new MoveTo(0, -1),
+            new LineTo(w, -1),
+            new LineTo(w, 0),
+            new LineTo(0, 0),
+            new LineTo(0, -1)
+        };
+    }
+
+    protected PathElement[] getRangeBottom() {
+        double w = getWidth();
+        double h = getHeight();
+        double h1 = h + 1.0;
+
+        return new PathElement[] {
+            new MoveTo(0, h),
+            new LineTo(w, h),
+            new LineTo(w, h1),
+            new LineTo(0, h1),
+            new LineTo(0, h)
+        };
+    }
