+    private void handleVScrollMouseEvent(MouseEvent ev) {
+        EventType<? extends MouseEvent> t = ev.getEventType();
+        if (t == MouseEvent.MOUSE_PRESSED) {
+            vsbPressed = true;
+        } else if (t == MouseEvent.MOUSE_RELEASED) {
+            vsbPressed = false;
+            updateVerticalScrollBar();
