+                arrangement().getUnwrappedWidth() +
+                snapSizeX(leftSide) +
+                snapSizeX(rightSide) +
+                contentPaddingLeft +
+                contentPaddingRight +
+                snapSizeX(Params.HORIZONTAL_GUARD);
