+                if (ttype != StreamTokenizer.TT_NUMBER) {
+                    //          System.out.println("Bad token: type=" + ttype + " tok=" + in.sval);
+                    throw new IOException("Unexpected token in character set file");
+                    //          continue;
+                }
+                values[i] = (char)(in.nval);
+                i++;
+            }
+        }
+
+        return values;
+    }
