+        String className = t.getClassName();
+        int ix = className.lastIndexOf('.');
+        if (ix >= 0) {
+            className = className.substring(ix + 1);
+        }
+        System.err.println(className + "." + t.getMethodName() + ":" + t.getLineNumber() + " " + msg);
+    }
